import React, { useEffect } from 'react'
import Quill from "quill"
import "quill/dist/quill.snow.css"

export default function TextEditor() {
    useEffect(() => {
        new Quill("#container", {
            theme: "snow",
            modules: {
                toolbar: [
                    [{ header: [1, 2, false] }],
                    ["bold", "italic", "underline"],
                    ["image", "code-block"],
                    ["clean"]
                ]
            }
        })
    }, [])
  return (
    <div id="container"></div>
  )
}
