import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { v4 as uuidv4 } from 'uuid';

function DocumentsHome() {
  const [documents, setDocuments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDocuments = async () => {
      try {
        // Replace with your actual API endpoint
        const response = await fetch("http://localhost:3001/api/documents"); 

        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        
        const data = await response.json();
        setDocuments(data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching documents:', err);
        setError('Failed to load documents. Please try again later.');
        setLoading(false);
      }
    };

    fetchDocuments();
  }, []);

  const createNewDocument = () => {
    // Navigate to a new document with a generated UUID
    window.location.href = `/documents/${uuidv4()}`;
  };

   if (loading) return (
        <div className="loadContainer">
                <div className="loader"></div>
        </div>);
  if (error) return <div className="text-center p-6 text-red-500">{error}</div>;

  return (
    <div className="homeContainer">
      <nav className="headerContainer">
        <h1 className="title">Your Documents</h1>
        <button 
          onClick={createNewDocument}
          className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded"
        >
          +
        </button>
      </nav>

      {documents.length === 0 ? (
        <div className="text-center p-10 bg-gray-50 rounded-lg">
          <p className="mb-4">You don't have any documents yet.</p>
          <button 
            onClick={createNewDocument}
            className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded"
          >
            Create Your First Document
          </button>
        </div>
      ) : (
        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
          {documents.map(doc => (
            <Link 
              to={`/documents/${doc._id}`} 
              key={doc._id}
              className="block p-4 border rounded-lg hover:shadow-md transition-shadow"
            >
              <h2 className="font-semibold truncate">{doc.title || "Untitled Document"}</h2>
              <p className="text-sm text-gray-500">
                Last edited: {new Date(doc.updatedAt).toLocaleString()}
              </p>
            </Link>
          ))}
        </div>
      )}
    </div>
  );
}

export default DocumentsHome;
